import React from 'react'
import ReactFauxDOM from 'react-faux-dom'
import * as d3 from 'd3'
import { directive } from '@babel/types';
 
class SVGIcicle extends React.Component {

    state = {
        data: this.props.data,
        width: 975,
        height: 2400,
      }
    
    partition(){
        data = data => d3.partition()
            .size([this.state.height, this.state.width])
            .padding(1)
      (d3.hierarchy(data)
        .sum(d => d.value)
        .sort((a, b) => b.this.state.height - a.this.state.height || b.value - a.value))
    }

    render() {
        let data = this.props.data;

        let margin = {top: 20, right: 20, bottom: 30, left: 40},
        width = this.props.width - margin.left - margin.right,
        height = this.props.height - margin.top - margin.bottom;
        
        //create element to mount on
        const div = new ReactFauxDOM.Element('div')

        let svg = d3.select(div).append("svg")
                    .style("width", "100%")
                    .style("height","auto")
                    .style("font", "10px sans-serif");


        return div.toReact()
    }
}
export default SVGIcicle;
