import React,{Component} from 'react';
import './App.css';
import axios from 'axios';
import Navbar from "./components/Navbar"
import Selektion from "./components/Selektion"
import Treemap from "./components/Treemap"
import Suchleiste from "./components/Suchleiste"
import Graphen from "./components/Graphen"
import AddSelect from "./components/AddSelect"
import Buttons from "./components/Buttons"



class App extends Component {
        state={
            contacts:[],
            selection:[ ]

}





  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      dropdownOpen: false
    };

  }


  toggle( ) {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }


// Delete Todo
  delSelect = (id) => {
    axios.delete(`https://jsonplaceholder.typicode.com/selection/${id}`)
      .then(res => this.setState({ selection: [...this.state.selection.filter(select => select.id !== id)] }));
  }


  // Add Todo
  addSelect = (title) => {
    axios.post('https://jsonplaceholder.typicode.com/selection', {
      title,
      completed: false
    })
      .then(res => this.setState({ selection: [...this.state.selection, res.data] }));
  }


    render() {

        return (

        <div className="App">
            <div id = "Navigation">
              <Navbar>
              </Navbar>
            </div>
            <body className="App-body">
              <div id = "Parts">

              <div id = "Links">
                  <div id = "Selektion">
                      <Selektion>
                      </Selektion>
                  </div>
                  <div id="Treemap">
                      <div id ="add">
                          <AddSelect></AddSelect>
                      </div>
                      <Treemap></Treemap>
                  </div>
              </div>
              <div id ="Rechts">
                  <div id = "Suchleiste">
                  <Suchleiste></Suchleiste>
                  </div>
                  <div id = "Graphen">
                      <Graphen></Graphen>
                  </div>
                  <div id="buttons">
                <Buttons></Buttons>
                  </div>
              </div>
              </div>
              </body>
        </div>
        );
  }
}
 export default App;
