import React, { Component } from 'react';
import PropTypes from 'prop-types';
import {Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Button} from 'reactstrap'

export class AddSelect extends Component {
  state = {
    title: ''
  }

  adding = (e) => {
    e.preventDefault();
    this.props.addSelect(this.state.title);
    this.setState({ title: '' });
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  render() {
      const { id, title } = this.props.selection;
    return (
       <Button onClick={this.props.delSelect.bind(this, id)} >ADD</Button>
    )
  }
}

// PropTypes
AddSelect.propTypes = {
  addSelect: PropTypes.func.isRequired
}

export default AddSelect
