import React,{Component} from 'react';
import PropTypes from "prop-types";
import {Dropdown, DropdownToggle, DropdownMenu, DropdownItem} from 'reactstrap';



export class SelectItem extends Component{


toggle() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }


    render(){
        const { id } = this.props.select;
        return(
            <Dropdown id="Dropdown"  isOpen={this.state.dropdownOpen} onChange={this.props.markComplete.bind(this, id)} toggle={this.toggle}>
                <DropdownToggle caret>
                  AND
                </DropdownToggle>
                <DropdownMenu>
                  <DropdownItem> AND </DropdownItem>
                  <DropdownItem> OR </DropdownItem>
                </DropdownMenu>
            </Dropdown>

        )
    }
}



//Proptypes
SelectItem.propTypes={
    selection: PropTypes.array.isRequired,
    markComplete: PropTypes.func.isRequired,
    delSelect: PropTypes.func.isRequired,
}


export default SelectItem
