import React, { Component } from 'react';
import PropTypes from 'prop-types';
import {Dropdown, DropdownToggle, DropdownMenu, DropdownItem}  from 'react-bootstrap/Dropdown'

export class AddSelect extends Component {
  state = {
    title: ''
  }

  adding = (e) => {
    e.preventDefault();
    this.props.addSelect(this.state.title);
    this.setState({ title: '' });
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  render() {
    return (
        <Dropdown id="Dropdown" isOpen={this.state.dropdownOpen} toggle={this.toggle}>
            <DropdownToggle caret>
              AND
            </DropdownToggle>
            <DropdownMenu>
              <DropdownItem > AND </DropdownItem>
              <DropdownItem> OR </DropdownItem>
            </DropdownMenu>
        </Dropdown>
    )
  }
}

// PropTypes
AddSelect.propTypes = {
  addSelect: PropTypes.func.isRequired
}

export default AddSelect
